import React, { Component } from 'react';
import './index.css'; 

class Square extends Component {
    state = { status : " " };

    handleChange = (status) => {
        this.setState({status});
    }

    render() { 
        return (<button className = "square" onClick = {() =>this.handleChange("X")}>
        {this.state.status}
        </button>  );
    }
}

export default Square;
