import React, { Component } from 'react';   
import Square from './square'
import './index.css';

class GameBoard extends Component {
    constructor(props) {
        super(props);
        this.state = {
          squares: Array(9).fill(null),
        };
      }

    handleChange = (currentValue)=>{
        console.log("Called");
    }

    render() {
        const status = 'Next player: X';
        const {squares} = this.state;
        return (
        <React.Fragment>
            <div className="status">{status}</div>
            <div className = "board-row">
                <Square value ='0' array = {squares} click = {this.handleChange}/>
                <Square value ='1' array = {squares} click = {this.handleChange}/>
                <Square value ='2' array = {squares} click = {this.handleChange}/>
            </div> 
            <div className = "board-row">
                <Square value ='3' array = {squares} click = {this.handleChange}/>
                <Square value ='4' array = {squares} click = {this.handleChange}/>
                <Square value ='5' array = {squares} click = {this.handleChange}/>
            </div> 
            <div className = "board-row">
                <Square value ='6' array = {squares} click = {this.handleChange}/>
                <Square value ='7' array = {squares} click = {this.handleChange}/>
                <Square value ='8' array = {squares} click = {this.handleChange}/>
            </div> 
        </React.Fragment>

    );
    }
}

export default GameBoard;
