import React, { Component } from 'react';
import './index.css'; 
import GameBoard from './gameBoard';

class Game extends Component {

    state = {
      history:[{squares:Array(9).fill(null)}],
      nextPlayerIsX:true
    }

    handleChange = (currentIndex)=>{
        const history = this.state.history;
        const current = history[history.length-1];
        const squares = current.squares.slice();
        if (this.checkWinner(squares) || squares[currentIndex]) {
            return;
          }
        squares[currentIndex] =this.state.nextPlayerIsX ? 'X':'O';

        this.setState({history:history.concat[{squares:squares}],
        nextPlayerIsX :!this.state.nextPlayerIsX});

    }

    checkWinner = (squares)=>{
      const lines = [
          [0,1,2],
          [3,4,5],
          [6,7,8],
          [0,3,6],
          [1,4,7],
          [2,5,8],
          [0,4,8],
          [2,4,6]
      ]

      for(let i=0;i<lines.length;i++)
      {
          const[a,b,c] = lines[i];
          console.log(squares[a]);
          if(squares[a] && squares[a] === squares[b] && squares[a] === squares[c])
              return squares[a];
      }
      return null;
  }
    render() {
      const history = this.state.history;

      const current = history[history.length-1].squares;

      let status;
      const winner = this.checkWinner(current);

        if(winner)
        {
            status = `Winner is ${winner}`;
            window.alert(`Winner is ${winner}`);
        }
        else
            status = this.state.nextPlayerIsX ? 'X' : '0';

        return (
        <div className="game">
        <div className="game-board">
          <GameBoard squares = {current} status = {status} click = {(index)=>this.handleChange(index)}/>
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
        </div>);
    }
}

export default Game;
