import React, { Component } from 'react';   
import Square from './square'
import './index.css';

class GameBoard extends Component {
    state = {
        squares: Array(9).fill(null),
        nextPlayerIsX:true
      }

    handleChange = (currentIndex)=>{
        const localSquares = this.state.squares.slice();
        localSquares[currentIndex] =this.state.nextPlayerIsX ? 'X':'O';
        if(!this.state.squares[currentIndex])
        this.setState({squares:localSquares,nextPlayerIsX :!this.state.nextPlayerIsX});
    }

    checkWinner = (squares)=>{
        const lines = [
            [0,1,2],
            [3,4,5],
            [6,7,8],
            [0,3,6],
            [1,4,7],
            [2,5,8],
            [0,4,8],
            [2,4,6]
        ]
        for(let i=0;i<lines.length;i++)
        {
            const[a,b,c] = lines[i];
            if(squares[a] && squares[a] === squares[b] && squares[a] === squares[c])
                return squares[a]
        }
        return null;
    }
    render() {
        const {squares} = this.state;
        const winner = checkWinner(squares);
        const nextPlayer = this.state.nextPlayerIsX?'X':'O';
        return (
        <React.Fragment>
            <div className="status">Next player: {nextPlayer}</div>
            <div className = "board-row">
                <Square index ='0' array = {squares} click = {this.handleChange}/>
                <Square index  ='1' array = {squares} click = {this.handleChange}/>
                <Square index  ='2' array = {squares} click = {this.handleChange}/>
            </div> 
            <div className = "board-row">
                <Square index  ='3' array = {squares} click = {this.handleChange}/>
                <Square index  ='4' array = {squares} click = {this.handleChange}/>
                <Square index  ='5' array = {squares} click = {this.handleChange}/>
            </div> 
            <div className = "board-row">
                <Square index  ='6' array = {squares} click = {this.handleChange}/>
                <Square index  ='7' array = {squares} click = {this.handleChange}/>
                <Square index  ='8' array = {squares} click = {this.handleChange}/>
            </div> 
        </React.Fragment>

    );
    }
}

export default GameBoard;
