import React, { Component } from 'react';   
import Square from './square'
import './index.css';

class GameBoard extends Component {
    // state = {
       // squares: Array(9).fill(null),
        //nextPlayerIsX:true
      //}

    // handleChange = (currentIndex)=>{
    //     const localSquares = this.state.squares.slice();
    //     if (this.checkWinner(localSquares) || localSquares[currentIndex]) {
    //         return;
    //       }
    //     localSquares[currentIndex] =this.state.nextPlayerIsX ? 'X':'O';
    //     this.setState({squares:localSquares,nextPlayerIsX :!this.state.nextPlayerIsX});
    // }

    renderSquare(index)
    {
        <Square index = {index} click = {handleChange} value ={squares[index]}/>
    }

    render() {
        const {squares,handleChange,status} = this.props;
        return (
        <React.Fragment>
            <div className="status">Next player: {status}</div>
            <div className = "board-row">
                {this.renderSquare(0)}
                <Square index  ='1' array = {squares} click = {handleChange}/>
                <Square index  ='2' array = {squares} click = {handleChange}/>
            </div> 
            <div className = "board-row">
                <Square index  ='3' array = {squares} click = {handleChange}/>
                <Square index  ='4' array = {squares} click = {handleChange}/>
                <Square index  ='5' array = {squares} click = {handleChange}/>
            </div> 
            <div className = "board-row">
                <Square index  ='6' array = {squares} click = {handleChange}/>
                <Square index  ='7' array = {squares} click = {handleChange}/>
                <Square index  ='8' array = {squares} click = {handleChange}/>
            </div> 
        </React.Fragment>

    );
    }
}

export default GameBoard;
